        public static ManagedBitmap2 MakeBitmap_32_32_4(byte[] raw)
        {
            return new ManagedBitmap2(32, 32, raw);
        }

        public static ManagedBitmap2 MakeBitmap_24_24_3(byte[] raw)
        {
